function changeActiveUser(){document.getElementById("player-one").classList.contains("active")?(document.getElementById("player-one").classList.remove("active"),document.getElementById("player-two").classList.add("active")):(document.getElementById("player-two").classList.remove("active"),document.getElementById("player-one").classList.add("active")),document.getElementById("home-score").classList.contains("score")?(document.getElementById("home-score").classList.remove("score"),document.getElementById("away-score").classList.add("score")):(document.getElementById("away-score").classList.remove("score"),document.getElementById("home-score").classList.add("score"))}function goal(){document.getElementById("home-score").classList.contains("score")?(homeGoals++,document.getElementById("home-score").innerHTML=homeGoals,audio.play()):(awayGoals++,document.getElementById("away-score").innerHTML=awayGoals,audio.play())}Mousetrap.bind("k",changeActiveUser),Mousetrap.bind("g",goal);var homeGoals=0,awayGoals=0,audio=new Audio("assets/lib/sounds/goal.mp3");